INSERT INTO Exercises (exercise_id, exercise_name, preview_image, description, constant_unit_of_measure) VALUES (1, 'Latzug', LOAD_FILE('/var/lib/mysql-files/images/Latzug.jpg'), 'Bei dieser Übung kannst du verschiedene Griffe verwenden. Setze dich aufrecht hin und beginne dann mit der Ausführung, indem du den Griff in Richtung deiner Brust ziehst, während du dich leicht nach hinten lehnst. Achte darauf, dass du deine Ellenbogen nach unten an deinen Körper heranziehst.', 'SxWdh');
INSERT INTO Exercises (exercise_id, exercise_name, preview_image, description, constant_unit_of_measure) VALUES (2, 'Latzugmaschine', LOAD_FILE('/var/lib/mysql-files/images/Latzugmaschine.jpg'), 'Stelle den Sitz so ein, dass du gerade so die Griffe erreichst. Achte darauf, dass deine Handrücken zu dir zeigen. Ziehe die Griffe runter, sodass sich deine Ellenbogen in Richtung deiner Seiten bewegen. ', 'SxWdh');
INSERT INTO Exercises (exercise_id, exercise_name, preview_image, description, constant_unit_of_measure) VALUES (3, 'Überzüge', LOAD_FILE('/var/lib/mysql-files/images/Ueberzuege.jpg'), 'Stelle den Kabelzug auf die höchste Stufe und greife die Stange etwa schulterbreit. Achte darauf, dass deine Handrücken zu dir zeigen. Gehe etwa zwei Schritte vom Kabelzug zurück. Halte deine Arme in Verlängerung deines Oberkörpers und beuge diesen nach vorne. Das Kabel sollte nun parallel zum Kabelzug verlaufen. Beginne nun mit der Ausführung, indem du die Stange nach unten drückst, während du dich aufrichtest. In der Endposition solltest du fast gerade stehen und deine Arme sollten an deinem Körper anliegen. ', 'SxWdh');
INSERT INTO Exercises (exercise_id, exercise_name, preview_image, description, constant_unit_of_measure) VALUES (4, 'Enges Rudern', LOAD_FILE('/var/lib/mysql-files/images/Enges_Rudern.jpg'), 'Achte darauf, dass deine Handrücken nach außen zeigen. Setze dich so hin, dass das Kabel auf Spannung ist, wenn du deine Arme in Verlängerung deines Oberkörpers hältst und diesen nach vorne beugst. Beginne nun mit der Ausführung, indem du den Griff in Richtung deines Bauches ziehst, während du dich aufrichtest. Zwischen deinem Oberarm und deinem Unterarm sollte nun in etwa ein 90° Winkel sein. Achte darauf, dass du deine Ellenbogen ebenfalls in Richtung Bauch und an deinen Körper heranziehst.', 'SxWdh');
INSERT INTO Exercises (exercise_id, exercise_name, preview_image, description, constant_unit_of_measure) VALUES (5, 'Breites Rudern', LOAD_FILE('/var/lib/mysql-files/images/Breites_Rudern.jpg'), 'Achte darauf, dass deine Handrücken nach außen zeigen. Setze dich so hin, dass das Kabel auf Spannung ist, wenn du aufrecht sitzt und deine Arme nahezu gestreckt hast. Deine Schultern sollten hierbei weitmöglichst vorne sein. Beginne nun mit der Ausführung, indem du den Griff in Richtung deiner Brust ziehst, während du deine Schulterblätter zusammendrückst. Achte hierbei darauf, dass du deine Ellenbogen seitlich und in etwa auf Brusthöhe hältst. ', 'SxWdh');
INSERT INTO Exercises (exercise_id, exercise_name, preview_image, description, constant_unit_of_measure) VALUES (6, 'Hyperextension', LOAD_FILE('/var/lib/mysql-files/images/Hyperextension.jpg'), 'Stelle die Hyperextensionbank so ein, dass sich das Polster etwa auf Hüfthöhe befindet. Bewege dann deinen Oberkörper soweit nach unten, dass in etwa ein 90° Winkel zwischen deinem Oberkörper und deinen Beinen entsteht. Krümme deinen Oberkörper hierbei ein. Richte dich danach wieder auf. Achte hierbei darauf, dass kein übermäßiges Hohlkreuz entsteht. Wenn du deinen Rücken auch während des Aufrichtens leicht gekrümmt behältst, kann das Helfen den Fokus auf den Po zu verlagern. Andernfalls wird hauptsächlich der Rückenstrecker trainiert. Außerdem kannst du mit der Armhaltung variieren. Hältst du die Arme in Verlängerung zum Oberkörper, erschwert das die Übungsausführung. Optional kannst du außerdem Gewichte in den Händen halten.', 'SxWdh');
INSERT INTO Exercises (exercise_id, exercise_name, preview_image, description, constant_unit_of_measure) VALUES (7, 'Schulterheben', LOAD_FILE('/var/lib/mysql-files/images/Schulterheben.jpg'), 'Positioniere den Kabelzug auf die niedrigste Stufe. Stelle dich seitlich neben den Kabelzug und halte den Griff so, dass das Kabel in etwa eine Linie mit deinem nahezu ausgestreckten Arm bildet. Deine Schulter sollte ein Stück runter in Richtung des Kabels gerichtet sein. Beuge dich leicht nach vorne und ziehe nun deine Schulter hoch in Richtung deines Kopfes.', 'SxWdh');
INSERT INTO Exercises (exercise_id, exercise_name, preview_image, description, constant_unit_of_measure) VALUES (8, 'Bankdrücken', LOAD_FILE('/var/lib/mysql-files/images/Bankdruecken.jpg'), 'Positioniere eine Hantelbank in flacher Ausrichtung. Achte darauf, dass eine Hantelablage für deine Langhantel vorhanden ist. Lege dich auf den Rücken unter die abgelegte Langhantel, sodass diese sich in etwa über deinem Gesicht befindet und greife diese dann. Achte darauf, dass du deine Schultern runter an die Bank drückst und auch dein Nacken bis zum Hinterkopf im Kontakt zur Bank bleibt. Dein Rücken sollte hierbei leicht gewölbt sein. Achte außerdem darauf, dass deine Handrücken in deine Richtung zeigen und dass du die Hantel fest umschließt. Dein Griff sollte so breit sein, dass deine Unterarme und die Hantel, wenn sie bis zur Brust abgesenkt wird, in etwa einen 90° Winkel bilden. Wenn du das überprüft hast und darauf achtest, dass deine Füße zur Stabilisierung in etwa im 90° Winkel zum Boden stehen, kannst du mit der Ausführung beginnen. Hebe hierzu die Langhantel aus der Halterung und positioniere sie so, dass deine Arme nahezu ausgestreckt sind und in etwa in einem 90° Winkel zu deinem Oberkörper stehen. Senke die Hantel nun leicht schräg runter bis sie mittig die Brust leicht berührt und drücke sie im gleichen Bewegungsmuster wieder hoch. Deine Ellenbogen bewegen sich beim Absenken leicht nach vorne, in Richtung deiner Seiten. Achte darauf, dass dein Handgelenk und dein Unterarm stets eine Linie bilden. Bei dieser Übung ist es besonders bei steigendem Gewicht sinnvoll eine Aufsichtsperson hinzuzuziehen, die beim Ablegen der Hantel helfen kann, falls du nicht mehr ausreichend Kraft hast. Achte jedoch darauf, die Hantel stets rechtzeitig abzulegen, bevor dir die Kraft ausgeht.', 'SxWdh');
INSERT INTO Exercises (exercise_id, exercise_name, preview_image, description, constant_unit_of_measure) VALUES (9, 'Schrägbankdrücken', LOAD_FILE('/var/lib/mysql-files/images/Schraegbankdruecken.jpg'), 'Der Fokus bei dieser Übung liegt auf dem oberen Bereich der Brust. Positioniere eine ungefähr im 30° Winkel aufgerichtete Hantelbank. Achte darauf, dass eine Hantelablage für deine Langhantel vorhanden ist. Setze dich unter die abgelegte Langhantel, sodass diese sich in etwa über deinem Gesicht befindet und greife diese dann. Achte darauf, dass du deine Schultern runter an die Bank drückst und dass dein Rücken hierbei leicht gewölbt ist, sodass du nicht ganz flach an der Lehne anliegst. Achte außerdem darauf, dass deine Handrücken in deine Richtung zeigen und dass du die Hantel fest umschließt. Dein Griff sollte so breit sein, dass deine Unterarme und die Hantel, wenn sie bis zur Brust abgesenkt wird, in etwa einen 90° Winkel bilden. Wenn du das überprüft hast und darauf achtest, dass deine Füße zur Stabilisierung in etwa im 90° Winkel zum Boden stehen, kannst du mit der Ausführung beginnen. Hebe hierzu die Langhantel aus der Halterung und positioniere sie so, dass deine Arme nahezu ausgestreckt sind und in etwa in einem 90° Winkel zu deinem Oberkörper stehen. Senke die Hantel nun leicht schräg runter bis sie mittig die Brust leicht berührt und drücke sie im gleichen Bewegungsmuster wieder hoch. Deine Ellenbogen bewegen sich beim Absenken nach vorne, in Richtung deiner Seiten. Achte darauf, dass dein Handgelenk und dein Unterarm stets eine Linie bilden. Bei dieser Übung ist es besonders bei steigendem Gewicht sinnvoll eine Aufsichtsperson hinzuzuziehen, die beim Ablegen der Hantel helfen kann, falls du nicht mehr ausreichend Kraft hast. Achte jedoch darauf, die Hantel stets rechtzeitig abzulegen, bevor dir die Kraft ausgeht.', 'SxWdh');
INSERT INTO Exercises (exercise_id, exercise_name, preview_image, description, constant_unit_of_measure) VALUES (10, 'Butterfly', LOAD_FILE('/var/lib/mysql-files/images/Butterfly.jpg'), 'Stelle den Sitz so ein, dass du mit in etwa waagerechten Oberarmen an den Polstern anliegen und die Griffe greifen kannst. Achte darauf, dass deine Handrücken zu dir zeigen. Drücke die Griffe nun soweit wie möglich zusammen.', 'SxWdh');
INSERT INTO Exercises (exercise_id, exercise_name, preview_image, description, constant_unit_of_measure) VALUES (11, 'Flys von unten', LOAD_FILE('/var/lib/mysql-files/images/Flys_von_unten.jpg'), 'Der Fokus bei dieser Übung liegt auf dem oberen Bereich der Brust. Stelle beide Kabelzüge auf die niedrigste Stufe. Stelle dich mittig zwischen die Kabelzüge und halte die Griffe so, dass deine Arme nahezu ausgestreckt in etwa eine Linie mit dem jeweiligen Kabel bilden. Achte darauf, dass dein Rücken gerade ist und deine Handrücken nach hinten zeigen. Ziehe die Griffe nun nach vorne hoch bis deine Arme in etwa waagerecht zum Boden und deine Hände möglichst nah beieinander sind.', 'SxWdh');
INSERT INTO Exercises (exercise_id, exercise_name, preview_image, description, constant_unit_of_measure) VALUES (12, 'Flys von oben', LOAD_FILE('/var/lib/mysql-files/images/Flys_von_oben.jpg'), 'Der Fokus bei dieser Übung liegt auf dem unteren Bereich der Brust. Stelle beide Kabelzüge auf die höchste Stufe. Stelle dich mittig zwischen die Kabelzüge, gehe leicht nach hinten in die Knie und lehne deinen Oberkörper etwas nach vorne. Halte die Griffe so, dass deine Arme nahezu ausgestreckt in etwa eine Linie mit dem jeweiligen Kabel bilden. Achte darauf, dass deine Handrücken nach hinten zeigen. Ziehe die Griffe nun nach vorne runter bis deine Arme in etwa senkrecht zum Boden und deine Hände möglichst nah beieinander sind.', 'SxWdh');
INSERT INTO Exercises (exercise_id, exercise_name, preview_image, description, constant_unit_of_measure) VALUES (13, 'Trizepsdrücken', LOAD_FILE('/var/lib/mysql-files/images/Trizepsdruecken.jpg'), 'Stelle den Kabelzug auf die höchste Stufe und greife die Stange etwa schulterbreit. Achte darauf, dass deine Handrücken zu dir zeigen und deine Oberarme am Körper anliegen. Lehne dich leicht nach vorne und drücke dann die Stange nach unten, sodass deine Arme nahezu gerade sind. ', 'SxWdh');
INSERT INTO Exercises (exercise_id, exercise_name, preview_image, description, constant_unit_of_measure) VALUES (14, 'Trizepsdrücken über Kopf', LOAD_FILE('/var/lib/mysql-files/images/Trizepsdruecken_ueber_Kopf.jpg'), 'Stelle den Kabelzug in etwa auf Hüfthöhe ein. Drehe dich mit dem Rücken zum Kabelzug. Greife das Seil, sodass die Handrücken nach außen und die Ellenbogen nach oben zeigen. Drücke dann das Seil nach oben, sodass deine Arme nahezu gerade sind. Deine Arme kannst du hierbei leicht drehen, sodass deine Handrücken am Ende in Richtung des Kabelzugs zeigen.', 'SxWdh');
INSERT INTO Exercises (exercise_id, exercise_name, preview_image, description, constant_unit_of_measure) VALUES (15, 'Hammercurls', LOAD_FILE('/var/lib/mysql-files/images/Hammercurls.jpg'), 'Stelle den Kabelzug auf die niedrigste Stufe und lege dich vor den Kabelzug auf den Rücken mit Füßen in Richtung des Kabelzug. Greife das Seil, sodass deine Oberarme neben deinem Körper liegen, deine Arme nahezu gestreckt sind und deine Handrücken nach außen zeigen. Der Vorteil ist der liegenden Position ist hierbei, dass deine Arme nun in etwa in einer Linie mit dem Kabel sind. Ziehe nun deine Hände nach oben an deinem Oberkörper entlang in Richtung deiner Brust. Die Position deiner Oberarme sollte sich nicht verändern. ', 'SxWdh');
INSERT INTO Exercises (exercise_id, exercise_name, preview_image, description, constant_unit_of_measure) VALUES (16, 'Kabelzugcurls', LOAD_FILE('/var/lib/mysql-files/images/Kabelzugcurls.jpg'), 'Stelle den Kabelzug auf die niedrigste Stufe und positioniere in etwa zwei Schritten Entfernung eine aufgerichtete Hantelbank in entgegengesetzte Richtung. Setze dich auf die Hantelbank und achte darauf, den Griff so zu greifen, dass deine Handrücken nach hinten, in Richtung Kabelzug, zeigen. Dein Arme sollte nun hinter deinem Rücken nahezu gestreckt sein und in etwa eine Linie mit dem Kabel bilden. Auf dem Kabel sollte Spannung sein. Beginne nun damit deine Hand nach vorne und oben zu dir heranzuziehen. Die Position deines Oberarms sollte sich nicht verändern. ', 'SxWdh');
INSERT INTO Exercises (exercise_id, exercise_name, preview_image, description, constant_unit_of_measure) VALUES (17, 'SZ-Curls', LOAD_FILE('/var/lib/mysql-files/images/SZ-Curls.jpg'), 'Positioniere eine ungefähr im 30° Winkel aufgerichtete Hantelbank. Setze dich nun mit dem Bauch zur Lehne und greife die SZ-Stange, sodass deine Arme nahezu gestreckt sind und in etwa einen 90° Winkel zum Boden bilden. Hebe nun die SZ-Stange nach vorne hin an. Die Position deiner Oberarme sollte sich nicht verändern. ', 'SxWdh');
INSERT INTO Exercises (exercise_id, exercise_name, preview_image, description, constant_unit_of_measure) VALUES (18, 'Schulterdrücken', LOAD_FILE('/var/lib/mysql-files/images/Schulterdruecken.jpg'), 'Stelle den Sitz so ein, dass du die Griffe mit in etwa senkrechtem Unterarm greifen kannst. Achte darauf, dass deine Handrücken zu dir zeigen. Drücke die Griffe nun hoch bis deine Arme nahezu ausgestreckt sind. Achte darauf, dass dein Handgelenk und dein Unterarm stets eine Linie bilden. ', 'SxWdh');
INSERT INTO Exercises (exercise_id, exercise_name, preview_image, description, constant_unit_of_measure) VALUES (19, 'Seitheben', LOAD_FILE('/var/lib/mysql-files/images/Seitheben.jpg'), 'Stelle den Sitz so ein, dass deine Arme an den Polstern anliegen und du die Griffe greifen kannst. Die Polster solltest du so eng einstellen, dass sie an deinen Armen anliegen, wenn deine Oberarme an deinem Körper anliegen. Hebe nun die Polster seitlich an, bis deine Oberarme in etwa parallel zum Boden sind.', 'SxWdh');
INSERT INTO Exercises (exercise_id, exercise_name, preview_image, description, constant_unit_of_measure) VALUES (20, 'Reverse Butterfly', LOAD_FILE('/var/lib/mysql-files/images/Reverse_Butterfly.jpg'), 'Positioniere eine ungefähr im 30° Winkel aufgerichtete Hantelbank. Setze dich nun mit dem Bauch zur Lehne und greife zwei Kurzhanteln, sodass deine Hände möglichst nah beieinander und deine Arme leicht gebeugt sind. Hebe die Kurzhanteln dann seitlich an bis deine Oberarme in etwa parallel zum Boden sind. Achte darauf, dass du deine Schulterblätter zusammendrückst und deine Ellenbogen nach oben zeigen. ', 'SxWdh');
INSERT INTO Exercises (exercise_id, exercise_name, preview_image, description, constant_unit_of_measure) VALUES (21, 'Beinpresse', LOAD_FILE('/var/lib/mysql-files/images/Beinpresse.jpg'), 'Positioniere deine Füße etwa hüftbreit auf der Platte. Stelle den Sitz so ein, dass der Winkel zwischen Ober- und Unterschenkel in etwa 90° beträgt. Wenn deine Beweglichkeit und das Gerät es zulassen, kannst du den Winkel noch weiter verkleinern. Strecke nun die Beine bis sie nahezu gerade sind. Achte darauf, dass die Knie nicht durchgestreckt werden und während der gesamten Übungsausführung gerade bleiben, sodass Ober- und Unterschenkel eine Linie bilden. Achte außerdem darauf möglichst aus den Fersen zu drücken und den Rücken nicht vom Sitz abheben zu lassen. Mit der Höhe der Fußposition auf der Platte kannst du variieren. Im oberen Bereich wird der Fokus auf den Po verlagert, während im unteren Bereich der Quadrizeps mehr Last übernimmt. ', 'SxWdh');
INSERT INTO Exercises (exercise_id, exercise_name, preview_image, description, constant_unit_of_measure) VALUES (22, 'Kickbacks', LOAD_FILE('/var/lib/mysql-files/images/Kickbacks.jpg'), 'Stelle den Kabelzug auf die niedrigste Stufe. Befestige die Fußschlaufe und gehe etwa ein bis zwei Schritte vom Kabelzug zurück. Beuge deinen Oberkörper nach vorne und halte dich zur Stabilisierung am Kabelzug fest. Hebe dein Bein und lasse es zunächst leicht gebeugt ein Stück in Richtung Kabelzug gehen. Tritt dann weitmöglichst nach hinten. Halte die Beugung während der gesamten Übungsausführung bei. Achte darauf, dass dein Rücken gerade bleibt. Optional kannst du dein Standbein außerdem auf einem Tritt positionieren, um den Bewegungsablauf zu erleichtern.', 'SxWdh');
INSERT INTO Exercises (exercise_id, exercise_name, preview_image, description, constant_unit_of_measure) VALUES (23, 'Hipthrust', LOAD_FILE('/var/lib/mysql-files/images/Hipthrust.jpg'), 'Positioniere eine Hantelbank in flacher Ausrichtung und lege eine Langhantel bereit. Positioniere deinen Rücken so, dass er in etwa auf Höhe der Schulterblätter aufliegt. Positioniere deine Beine so, dass der Winkel zwischen Ober- und Unterschenkel in etwa 90° beträgt. Lege die Langhantel in etwa auf Höhe deiner Hüftknochen. Hierzu empfiehlt sich bei steigendem Gewicht die Verwendung eines Polsters. Bewege nun deine Hüfte nach unten und stoße sie danach wieder hoch. Achte darauf, dass dein Rücken auch während des Runtergehens gerade bleibt.', 'SxWdh');
INSERT INTO Exercises (exercise_id, exercise_name, preview_image, description, constant_unit_of_measure) VALUES (24, 'Beinstrecker', LOAD_FILE('/var/lib/mysql-files/images/Beinstrecker.jpg'), 'Stelle das Polster so ein, dass es sich direkt über deinen Füßen befindet. Das Gelenk des Geräts sollte sich auf Höhe deines Kniegelenks befinden. Zwischen deinen Ober- und Unterschenkeln sollte in etwa ein 90° Winkel sein. Wenn deine Beweglichkeit und das Gerät es zulassen, kannst du deine Unterschenkel noch weiter nach hinten bringen. Strecke nun deine Beine nach oben.', 'SxWdh');
INSERT INTO Exercises (exercise_id, exercise_name, preview_image, description, constant_unit_of_measure) VALUES (25, 'Beinbeuger', LOAD_FILE('/var/lib/mysql-files/images/Beinbeuger.jpg'), 'Stelle das Polster so ein, dass es sich auf der Rückseite deines Beines direkt über deinen Füßen befindet. Das Gelenk des Geräts sollte sich auf Höhe deines Kniegelenks befinden. Deine Ober- und Unterschenkel sollten in etwa eine Linie bilden. Beuge nun deine Beine nach unten bis zwischen deinen Ober- und Unterschenkeln in etwa ein 90° Winkel ist. ', 'SxWdh');
INSERT INTO Exercises (exercise_id, exercise_name, preview_image, description, constant_unit_of_measure) VALUES (26, 'Wadenheben stehend', LOAD_FILE('/var/lib/mysql-files/images/Wadenheben_stehend.jpg'), 'Stelle dich gerade, etwa hüftbreit mit den Fußballen auf einen Tritt. Deine Fersen schweben kurz über dem Boden. Hebe dann deine Waden an, indem du dich mit deinen Fußballen auf dem Tritt abdrückst und deine Fersen hochziehst. Für zusätzliches Gewicht, kannst du Kurzhanteln während der Übungsausführung in den Händen halten. Für eine höhere Stabilität kannst du die Übung einbeinig ausführen und dich bei Bedarf festhalten. ', 'SxWdh');
INSERT INTO Exercises (exercise_id, exercise_name, preview_image, description, constant_unit_of_measure) VALUES (27, 'Wadenheben sitzend', LOAD_FILE('/var/lib/mysql-files/images/Wadenheben_sitzend.jpg'), 'Positioniere eine Hantelbank in flacher Ausrichtung und davor einen Tritt. Lege außerdem eine Langhantel bereit. Setze dich auf die Bank, sodass zwischen deinen Ober- und Unterschenkeln in etwa ein 90° Winkel entsteht und deine Füße etwa hüftbreit stehen. Richte den Tritt so aus, dass er unter deinen Fußballen steht. Deine Fersen schweben kurz über dem Boden. Positioniere nun die Langhantel auf deinen Knien. Hebe dann deine Waden an, indem du dich mit deinen Fußballen auf dem Tritt abdrückst und deine Fersen hochziehst.', 'SxWdh');
INSERT INTO Exercises (exercise_id, exercise_name, preview_image, description, constant_unit_of_measure) VALUES (28, 'Crunchmaschine', LOAD_FILE('/var/lib/mysql-files/images/Crunchmaschine.jpg'), 'Halte die Griffe fest und setzte dich gerade, mit leichtem Hohlkreuz hin. Krümme nun deinen Oberkörper weitmöglichst ein. Achte darauf, dass die Krümmung nur im Oberkörper stattfindet und die Hüfte nicht mit involviert wird. ', 'SxWdh');
INSERT INTO Exercises (exercise_id, exercise_name, preview_image, description, constant_unit_of_measure) VALUES (29, 'Beinheben', LOAD_FILE('/var/lib/mysql-files/images/Beinheben.jpg'), 'Positioniere dich im Gerät mit deinem Rücken an der Lehne, deinen Unterarmen auf den Polstern und den Griffen fest in der Hand. Lasse deine Beine nahezu ausgestreckt hängen. Hebe die Beine möglichst weit an, sodass du deine Knie in Richtung deines Bauches ziehst. Dein Oberkörper soll im Bereich des Bauches weitmöglichst eingekrümmt werden.', 'SxWdh');
