INSERT INTO Exercises (exercise_id, exercise_name, preview_image, description, constant_unit_of_measure) VALUES (34, 'Liegestütz', LOAD_FILE('/var/lib/mysql-files/images/Liegestuetz.jpg'), 'Positioniere deine Hände etwa schulterbreit auf dem Boden und stelle deine Füße möglichst nah nebeneinander. Achte darauf, dass dein Körper ausreichend unter Spannung steht, sodass er eine gerade Linie bildet. Senke deinen Körper nun in Richtung Boden ab, indem du deine Ellenbogen schräg nach hinten bewegst. Versuche dem Boden so nah wie möglich zu kommen, ohne dass du dein Gewicht ablegst. Für den Einstieg empfiehlt es sich die Füße etwas breiter zu positionieren, um einen stabileren Stand zu erreichen. Alternativ kannst du auch deine Knie anstelle deiner Füße auf dem Boden positionieren oder deine Hände auf einer Erhöhung abstützen, um die Ausführung zu erleichtern. Zur Entlastung deiner Handgelenke empfiehlt es sich außerdem Liegestützgriffe zu  verwenden.', 'Min');
INSERT INTO Exercises (exercise_id, exercise_name, preview_image, description, constant_unit_of_measure) VALUES (35, 'Schulterrotation', LOAD_FILE('/var/lib/mysql-files/images/Schulterrotation.jpg'), 'Hebe deine Arme seitlich an, sodass deine Oberarme eine Linie parallel zum Boden bilden. Deine Unterarme sollten ebenfalls parallel zum Boden und in etwa in einem 90° Winkel zu deinen Oberarmen sein. Drehe deine Arme nun so nach oben, dass deine Unterarme im 90° Winkel zum Boden stehen. Der Winkel zwischen deinen Ober- und Unterarmen sollte hierbei unverändert bleiben. Senke deine Arme danach wieder in die Ausgangsposition ab. ', 'Min');
INSERT INTO Exercises (exercise_id, exercise_name, preview_image, description, constant_unit_of_measure) VALUES (36, 'Kniebeuge', LOAD_FILE('/var/lib/mysql-files/images/Kniebeuge.jpg'), 'Stelle dich gerade hin. Stelle deine Beine etwa schulterbreit auf und drehe deine Fußspitzen leicht außen. Gehe nun in die Hocke bis der Winkel zwischen Ober- und Unterschenkel in etwa 90° beträgt. Wenn deine Beweglichkeit es zulässt, kannst du auch noch tiefer in die Hocke gehen. Achte darauf, dass du deine Hüfte nach hinten schiebst und deinen Rücken in sich gerade lässt, sodass sich dein Kopf senkrecht in Richtung deiner Knie bewegt. Zur besseren Balance kannst du deine Arme vor deinem Körper ausstrecken.', 'SxWdh');
INSERT INTO Exercises (exercise_id, exercise_name, preview_image, description, constant_unit_of_measure) VALUES (37, 'Klimmzüge', LOAD_FILE('/var/lib/mysql-files/images/Klimmzuege.jpg'), 'Greife die Griffe breiter als schulterbreit und achte darauf, dass deine Handrücken zu dir zeigen. Lass dich nach unten hin weitmöglichst aushängen und ziehe dich dann möglichst gerade nach oben, bis dein Kinn in etwa auf Höhe der Griffe ist. Drücke beim Hochziehen deine Schulterblätter zusammen und deine Ellenbogen nach unten in Richtung deiner Seiten. Für den Einstieg empfiehlt es sich ein Gerät mit Gegengewicht zu verwenden. Alternativ kannst du auch ein Teil deines Gewichtes mit deinen Beinen abstützen.', 'Min');
